<template>    <div class="title">        <span class="quit" @click="cancel">取消</span>        {{title}}        <span class="submit" @click="submit">确定</span>    </div></template><script>    import util from '../../../api/util'    export default {        data() {            return {                select:0            }        },        props:{            title:{                type:String            }        },        methods: {            cancel(){                this.$emit('cancel')            },            submit(){                this.$emit('confirm')            }        }    }</script><style lang="less" scoped>    .title {        width: 100%;        display: flex;        justify-content: space-between;        padding: 0 .1rem;        font-size: 0.16rem;        color: #2C2C2C;        border-bottom: 0.01rem solid #ddd;        height: .4rem;        line-height: .4rem;        span {            color: #EF4F4F;        }    }</style>