<template>    <div class="page">        <mt-header title="我的订单">            <router-link to="/" slot="left">                <mt-button icon="back"></mt-button>            </router-link>        </mt-header>        <!--tab导航栏-->        <top-bar ref="bar" :list="nav" @change="getInitOrder"></top-bar>        <div class="container">            <!--tab列表-->            <list-view @loadMore="loadorder" :loading="loading">                <order-item v-for="(i,index) in list" :v="i" :key="index" @cancel="cancel" @express="express" @confirm="confirm"                            @remind="remind" @del="del"></order-item>            </list-view>        </div>        <group-bar></group-bar>        <transition name="slide">            <router-view></router-view>        </transition>    </div></template><script>    // import util from '../../../api/util'    import list from './../../../assets/mixins/list'    import order from './../../../assets/mixins/order'    import listView from '@/components/common/view/listView'    // import orderItem from '@/components/order/index/order.vue'    import orderItem from './orderItem.vue'    import TopBar from '@/components/common/Tabbar/TopBar'    import groupBar from '../../common/Tabbar/GroupBar.vue'    import {MessageBox, Toast} from 'mint-ui'    export default {        data() {            return {                select: '',                url: 'groups.order',                nav: [                    {                        title: '全部',                        status: ''                    },                    {                        title: '待付款',                        status: '0'                    },                    {                        title: '待发货',                        status: '1'                    },                    {                        title: '待收货',                        status: '2'                    },                    {                        title: '已完成',                        status: '3'                    },                    {                        title: '退换货',                        status: '4'                    },                ]            }        },        mixins: [list, order],        methods: {            getInitOrder(s) {                this.select = s                let params = {                    url: 'groups.order.lists',                    opt: {                        status: s,                    }                };                this.scrollTop('.container').then(this.listInit()).then(this.getList(params))            },            loadorder() {                let _this = this;                let params = {                    url: 'groups.order.lists',                    opt: {                        status: _this.select,                    }                };                this.loadMore(params)            },            express(id) {		      this.$router.push({ name: "logistics", query: { id: id ,name:2} });		    },        },        beforeRouteUpdate(to, from, next) {            if (from.query.stal===0) {                this.getInitOrder(from.query.a);            }            next()        },        mounted() {            console.log('mounted')            this.getInitOrder('');            this.$refs.bar.select = 0        },        components: {            listView,            orderItem,            TopBar,            groupBar        }    }</script><style lang="less" scoped>    @import '../../../assets/less/index.less';    .page {        .page-view(1);        .container {            .scroll-view(100%);            background: #f7f7f7;            //            .header-height;            .list {                padding-bottom: .8rem;            }        }    }</style>