<template>    <li class="tab-list">        <router-link :to="{name:'GroupOrderDetail',query:{oid:v.id,tid:v.teamid}}" class="list-title" tag="div">            <div>订单号:{{v.orderno}}</div>            <div>{{v.statusstr}} <span class="icon-you">&#xe61b;</span></div>        </router-link>        <router-link class="list-main" :to="{name:'GroupOrderDetail',query:{oid:v.id,tid:v.teamid}}" tag="div">            <div class="main-left">                <img :src="v.thumb" alt="">            </div>            <div class="main-center">                {{v.title}}            </div>            <div class="main-right">                <div>                    <span>￥{{v.is_team=='1'?v.groupsprice:v.singleprice}}/{{v.goodsnum>1?v.goodsnum:''}}{{v.units}}</span>                </div>                <div>x 1</div>            </div>        </router-link>        <div class="list-nav">            运费: <span>￥{{v.freight}}</span>元,            共 <span>{{v.goodsnum}}</span>个商品,总额: <span>￥{{v.amount}}</span>元            <i class="icon-you">&#xe61b;</i>        </div>        <div class="list-btn">            <div v-if="v.status==0" class="btns act" @click="cancel(v.id)">取消订单</div>            <router-link class="btns" :to="{name:'pay',query:{oid:v.id,tid:v.teamid,type:'groups'}}"  v-if="v.status==0" tag="div">支付订单</router-link>            <div class="btns act" v-if="v.status==2" @click="express(v.id)">查看物流</div>
            <div class="btns" v-if="v.status==2" @click="confirm(v.id)">确认收货</div>            <div v-if="v.status==-1" class="btns act" @click="del(v.id)">删除订单</div>        </div>    </li></template><script>    export default {        data () {            return {            }        },        props: {            v: {               type: Object,                required: true            },        },        mounted () {        },        methods:{            cancel(i) {                this.$emit('cancel', i)            },            express(i) {                this.$emit('express', i)            },            confirm(i) {                this.$emit('confirm', i)            },            del(i) {                this.$emit('del', i)            },            remind(i) {                this.$emit('remind', i)            },            goDetail(i){                this.$emit('router',i)            },            logistic(){                this.$emit('logistic')            }        }    }</script><style lang="less" scoped>    @import '../../../assets/less/common.less';    .tab-list {        background: #ffffff;        margin-top: .08rem;        font-size: .12rem;        .list-title {            display: flex;            justify-content: space-between;            padding: .08rem;        }        .list-main {            display: flex;            justify-content: space-between;            padding: .12rem;            background: #f7f7f7;            .main-left {                width: .6rem;                height: .6rem;                img {                    width: 100%;                    height: 100%;                }            }            .main-center {                flex: 1;                text-align: left;                margin-left: .1rem;                font-size: .14rem;                color: #727272;            }            .main-right {                display: flex;                width: 18%;                align-items: center;                flex-direction: column;                font-size: .14rem;                > div:first-child span {                    color: @themeColor;                    font-size: .14rem;                }                > div:last-child {                    width: 100%;                    text-align: right;                    font-size: .14rem;                }            }        }        .list-nav {            padding: 0.12rem;            text-align: right;            font-size: .14rem!important;            span {                color: @themeColor;                font-size: .14rem!important;            }        }        .list-btn {            border-top: 1px solid #f7f7f7;            padding: .12rem;            display: flex;            justify-content: flex-end;            .act{                background: #ffffff!important;                border: 1px solid @themeColor!important;                color: @themeColor!important;            }            .btns {                margin-left: .08rem;                display: inline-block;                min-width: .6rem;                padding: .04rem .08rem;                box-sizing: border-box;                color: #ffffff;                background: @themeColor;                border-radius: .03rem;            }        }    }</style>