<template>    <div class="sort">        <div :class="['item',{active:info.order == ''}]" @click="sort('')">综合</div>        <div :class="['item',{active:info.order == 'sales'}]" @click="sort({order : 'sales'})">销量</div>        <div class="item">            <span :class="{active:info.order == 'minprice'}"                  @click="sort({order : 'minprice',by: info.by === 'asc'? 'desc':'asc'})">价格</span>            <div class="icon">                <span :class="['iconfont','up',{active:info.by === 'asc'}]">&#xe667;</span>                <span :class="['iconfont','down',{active:info.by === 'desc'}]">&#xe666;</span>            </div>        </div>        <div :class="['item',{active:sorts}]" @click="screenGoods"><span>筛选</span><span                class="iconfont sicon">&#xe66a;</span>        </div>    </div></template><script>    import util from '../../../api/util'    export default {        props: {            info: {                type: Object,                required: true,                default() {                    return {}                }            }        },        methods: {            sort(o) {                let params = {                    keywords: '',                    by: '',                    order: ''                }                for (let i in o) {                    params[i] = o[i]                    if (i !== 'by') {                        params['by'] = ''                    }                }                console.log(params)                this.$emit('change', params)            },            screenGoods() {                this.$emit('toggle')            }        },        components: {//            screen        },        mounted() {            console.log('this.sorts')            console.log(this.sorts)        },        computed: {            sorts() {                for (var i in this.info) {                    if (this.info[i] && i !== 'order' && i !== 'by') {                        return true                    }                }                return false            }        }    };</script><style lang="less" scoped>    @import "../../../assets/less/common.less";    .sort {        width: 100%;        display: flex;        padding: .065rem 0;        background: #fff;        -webkit-align-items: center;        align-items: center;        display: -webkit-flex;        display: flex;        z-index: 3;        position: relative;        border-top: @border;        border-bottom: @border;        .item {            position: relative;            display: flex;            -webkit-flex: 1;            flex: 1;            position: relative;            text-align: center;            font-size: .14rem;            border-left: 1px solid #e7e7e7;            color: #666;            vertical-align: middle;            z-index: 2;            justify-content: center;            align-items: center;            span {                font-size: .14rem;            }            .icon {                display: flex;                flex-direction: column;                height: .2rem;                margin-left: .02rem;                .active {                    color: red;                }            }            .down {                position: absolute;                top: .07rem;            }            .sicon {                position: relative;                top: 0.01rem;                left: .02rem;            }            .iconfont {                color: #666;            }        }        .item:first-child {            border-left: none;        }        .active {            color: red;        }    }</style>