<template>    <div class="page">        <mt-header title="选择地址" fixed>            <a slot="left" @click="goBack">                <mt-button icon="back"></mt-button>            </a>        </mt-header>        <div class="container">            <list-view>                <store-info v-for="(i,index) in list" :key="index" :info="i" @click="save(i)"></store-info>            </list-view>        </div>    </div></template><script>    import listView from '../../common/view/listView.vue'    import storeInfo from './storeInfo.vue'    import util from '../../../api/util'    import {mapState, mapMutations, mapGetters} from 'Vuex';    export default {        data(){          return {              list:[]          }        },        methods: {            init(){                const _this=this                util.request({                    url:'store.selector',                    method:'get',                    data:{                        ids:''                    },                    success(res){                        console.log(res)                        _this.list=res.data.list                    }                })            },            save(i){//                console.log(111)                this.STORE(i)                console.log('this.store(i)')                this.$route.query.store=1                this.$router.go(-1)            },            ...mapMutations([                'STORE'            ]),            goBack(){                this.$router.go(-1)            }        },        mounted(){            this.init()            console.log('mounted')        },        components: {            listView,            storeInfo        }    }</script><style lang="less" scoped>    @import '../../../assets/less/index.less';    .page{        .page-view(100);        .container {            .scroll-view(100%);            .header-height        }    }</style>