<template>    <li class="tab-list">        <router-link :to="{name:'detail',query:{id:item.id}}" class="list-title" tag="div">            <div>订单号:{{item.ordersn}}</div>            <div>{{item.statusstr}} <span class="icon-you">&#xe61b;</span></div>        </router-link>        <template v-for="v in item.goods">            <router-link class="list-main" :to="{name:'detail',query:{id:item.id}}" v-for="(s,x) in v.goods" :key="x"                         tag="div">                <div class="main-left">                    <img :src="s.thumb" alt="">                </div>                <div class="main-center">                    <div>{{s.title}}</div>                    <div v-if="s.optiontitle">规格:{{s.optiontitle}}</div>                </div>                <div class="main-right">                    <div>                        <span>￥{{s.price}}</span>                    </div>                    <div>x{{s.total}}</div>                </div>            </router-link>        </template>        <div class="list-nav">            <!--运费: <span>￥0.01</span>元,-->            共 <span>{{item.goods[0].goods.length}}</span>个商品,总额: <span>￥{{item.price}}</span>元            <i class="icon-you">&#xe61b;</i>        </div>        <div class="list-btn">            <div v-if="item.cancancel " class="btns act" @click="cancel(item.id)">取消订单</div>            <router-link class="btns" :to="{name:'pay',query:{id:item.id,type:'order'}}" v-if="item.canpay" tag="div">                支付订单            </router-link>            <div class="btns" v-if="item.cancomplete" @click="confirm(item.id)">确认收货</div>            <div class="btns" v-if="item.hasexpress" @click="logistic(item.id)">查看物流</div>            <div v-if="item.candelete" class="btns act" @click="del(item.id)">删除订单</div>            <div v-if="item.canverify" class="btns act" @click="goDetail(item.id)">我要取货</div>        </div>    </li></template><script>    import util from '../../../api/util'    import defaultImg from '../../../assets/images/order-tip.png'    export default {        data() {            return {}        },        props: {            item: {                type: Object,                required: true            },        },        methods: {            cancel(i) {                this.$emit('cancel', i)            },            confirm(i) {                this.$emit('confirm', i)            },            del(i) {                this.$emit('del', i)            },            remind(i) {                this.$emit('remind', i)            },            goDetail(i){                this.$emit('router',i)            },            logistic(){                this.$emit('logistic')            }        },    }</script><style lang="less" scoped>    @import '../../../assets/less/common.less';    @font-face {        font-family: 'iconfont';  /* project id 519918 */        src: url('//at.alicdn.com/t/font_519918_wmq7jneofrms4i.eot');        src: url('//at.alicdn.com/t/font_519918_wmq7jneofrms4i.eot?#iefix') format('embedded-opentype'),        url('//at.alicdn.com/t/font_519918_wmq7jneofrms4i.woff') format('woff'),        url('//at.alicdn.com/t/font_519918_wmq7jneofrms4i.ttf') format('truetype'),        url('//at.alicdn.com/t/font_519918_wmq7jneofrms4i.svg#iconfont') format('svg');    }    .icon-you {        font-family: iconfont;    }    .list {        padding-bottom: .8rem;    }    .tab-list {        background: #ffffff;        margin-top: .08rem;        font-size: .12rem;        .list-title {            display: flex;            justify-content: space-between;            padding: .08rem;        }        .list-main {            display: flex;            justify-content: space-between;            padding: .12rem;            background: #f7f7f7;            flex-grow: 1;            .main-left {                width: .6rem;                height: .6rem;                img {                    width: 100%;                    height: 100%;                }            }            .main-center {                div {                    font-size: .14rem;                }                div:first-child {                    /*height: .38rem;*/                    overflow: hidden;                }                div:last-child {                    margin-top: .04rem;                    font-size: .12rem;                    color: #727272;                }                flex: 1;                text-align: left;                margin-left: .1rem;            }            .main-right {                display: flex;                width: 18%;                align-items: center;                flex-direction: column;                > div:first-child span {                    color: @themeColor;                }                > div:last-child {                    width: 100%;                    text-align: right;                }            }        }        .list-nav {            padding: 0.12rem;            text-align: right;            span {                color: @themeColor;            }        }        .list-btn {            border-top: 1px solid #f7f7f7;            padding: .12rem;            display: flex;            justify-content: flex-end;            .act {                background: #ffffff !important;                border: 1px solid @themeColor !important;                color: @themeColor !important;            }            .btns {                margin-left: .08rem;                display: inline-block;                min-width: .6rem;                padding: .04rem .08rem;                box-sizing: border-box;                color: #ffffff;                background: @themeColor;                border-radius: .03rem;            }        }    }</style>